function revelpassword() {
  var x = document.getElementById("password");
  if (x.type === "password") {
    x.type = "text";
  } else {
    x.type = "password";
  }
}

const tooltipTriggerList = document.querySelectorAll('[data-bs-toggle="tooltip"]')
const tooltipList = [...tooltipTriggerList].map(tooltipTriggerEl => new bootstrap.Tooltip(tooltipTriggerEl))

$(document).ready(function () {
  $('.sidebar-toggle').on('click', function () {
    $('body').toggleClass('sidebar-hide');
  });

  $('body').on('click', '.menu-toggle', function () {
    $('body').toggleClass('menu-open');
    $('body').append("<div class='overlay'></div>");
  });
  $('body').on('click', '.close-menu,.overlay', function () {
    $('body').removeClass('menu-open');
    $(".overlay").remove();
  });

  $('.theme-toggle a').on('click', function () {
    $('.theme-toggle a').removeClass('active');
    $(this).addClass('active');
    $('body').toggleClass('dark-theme');
  });
  $('.revision-toggle').on('click', function () {
    $(this).next('.revision-body').slideToggle();
  });

   $('.project-tips').hover( function () {
    $('body').toggleClass('active-project-tips');
  });
});